import React from "react";
import { useCallback } from "react";
import { useUserAuth } from "../../contexts/AuthContext";
import { Container, Card, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from "react-router-dom"
import { WebPlaybackSDK, useWebPlaybackSDKReady } from "react-spotify-web-playback-sdk";
import SongTitle from './SongTitle'
import PauseResumeButton from './PauseResumeButton'


function Playback() {
    const { user, logout } = useUserAuth();
    const navigate = useNavigate();
    
    const AUTH_TOKEN = "BQCkKnQ-WzP26sCUNbLJZseRT-uzQAM84sDqHpRxXbv4PCjFMG6mvP5kf_veyczmkagGCiDiK8BBR7KPa1eg4tYILlWqE-pFWsKe4TUQBaa80qnXN84sOt8Z-EMrhZAWRO01OYpuO4xgRr_Q8_7nULXfv-Y0nYjI-KbQ7gAmHSagIHiKUsXBVl94g0VyjqBbm_0RT2H62uIj3g"; //<------------------------------- PUT SPOTIFY API TOKEN HERE. IF YOU NEED TO GENERATE ONE, USE THIS LINK: https://developer.spotify.com/documentation/web-playback-sdk/quick-start/
    const getOAuthToken = useCallback(callback => callback(AUTH_TOKEN), []);

    const MyPlayer = () => {
        const webPlaybackSDKReady = useWebPlaybackSDKReady();

        if (!webPlaybackSDKReady) return <div>Loading...</div>;

        return <div>Spotify App is ready!</div>;
    };

    
    
    
        return (
            <WebPlaybackSDK
                deviceName="My awesome Spotify app"
                getOAuthToken={getOAuthToken}
                volume={0.5}>
                <MyPlayer />
                
                <SongTitle />
            </WebPlaybackSDK>
        );
    };

export default Playback;
