import React from 'react';
import { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Nav } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Card, Button, Form, Alert } from 'react-bootstrap';
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { useAuth } from "../contexts/AuthContext"

function Signup() {
    const [user, setUser] = useState();
    const usernameRef = useRef();
    const emailRef = useRef();
    const passwordRef = useRef();
    const [error, setError] = useState();
    const [loading, setLoading] = useState();
    const { signup } = useAuth();

    async function handleSubmit() {


            setError("");
            setLoading(true);
              await signup(emailRef.current.value, passwordRef.current.value)
        .then((userCredential) => {
            // Signed in 
            const user = userCredential.user;
            // ...
        })
        

        setLoading(false);
    }



    return (
        <Container className="d-flex align-items-center justify-content-center"
            style={{ minHeight: "70vh" }}        >
            <Card className="w-50 mt-3">
                <Card.Title className="text-center mt-3">Create an Account</Card.Title>
                <Card.Body className="text-left">
                    {error && <Alert variant="danger">{error}</Alert>}
                    <Form className="w-100 text-left mt-3" onSubmit={handleSubmit}>

                        <Form.Group id="email">
                            <Form.Label>Email</Form.Label>
                            <Form.Control type="email" ref={emailRef} required maxLength={50} ></Form.Control>
                        </Form.Group>
                        <Form.Group id="username">
                            <Form.Label>Username</Form.Label>
                            <Form.Control type="username" ref={usernameRef} required maxLength={20} ></Form.Control>
                        </Form.Group>
                        <Form.Group id="password">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" ref={passwordRef} required maxLength={30} minLength={10}></Form.Control>
                            <Form.Text id="passwordHelpBlock" muted>Password must be 10 characters long.</Form.Text>
                        </Form.Group>

                        <Button className="btn primary-btn mt-1" type="submit" disabled={loading}> {loading ? 'Loading' : 'Signup'} </Button>

                    </Form>
                </Card.Body>
            </Card>
        </Container>
    )
}
export default Signup;
