import React from 'react';
import { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Nav } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Card, Button, Form } from 'react-bootstrap';

function Login() {
    const [user, setUser] = useState();
    const usernameRef = useRef();
    const passwordRef = useRef();

    function handleSubmit() {

    }

    return (
        <Container className="d-flex align-items-center justify-content-center"
            style={{ minHeight: "70vh" }}        >
            <Card className="w-100 text-left mt-3">
                <Card.Body>
                    <Form onSubmit={handleSubmit}>
                    <Form.Group id="username">
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="username" ref={usernameRef} required maxLength={20} ></Form.Control>
                    </Form.Group>
                    <Form.Group id="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" ref={passwordRef} required maxlength={30} minLength={10}></Form.Control>
                        <Form.Text id="passwordHelpBlock" muted>Password must be 10 characters long.</Form.Text>
                    </Form.Group>
                    <Button className="btn primary-btn mt-1">Login</Button>
                    </Form>
                    <Button className="btn  mt-1">Signup</Button>
                </Card.Body>
            </Card>
        </Container>
        )
}
export default Login;
